commands.code:
top: valeur;
right: valeur;
bottom: valeur;
left: valeur;

/* Ombre avec décalage horizontal et vertical */
box-shadow: 10px 10px;

/* Ombre avec décalage, flou et couleur */
box-shadow: 5px 5px 10px #888;

/* Ombre interne */
box-shadow: inset 0 0 10px #000;

.box {
	/* Largeur de la bordure de 2px */
	border-width: 2px;

	/* Bordure pleine */
	border-style: solid;

	/* Bordure noire */
	border-color: #000;

	/* Coins arrondis de 5px */
	border-radius: 5px;
}

/* Masque le contenu débordant */
.box {
	overflow: hidden;
}

/*Barres de défilement horizontales, et verticales si nécessaires */
.box {
	overflow-x: scroll;
	overflow-y: auto;
}

/* Aucune transformation */
transform: none;

/* Matrice 2D */
transform: matrix(a, b, c, d, tx, ty);

/* Matrice 3D */
transform: matrix3d(a1, b1, c1, d1, a2, b2, c2, d2, a3, b3, c3, d3, a4, b4, c4, d4);

/* Déplacement */
transform: translate(tx, ty);
transform: translateX(tx);
transform: translateY(ty);
transform: translateZ(tz);
transform: translate3d(tx, ty, tz);

/* Mise à l'échelle */
transform: scale(sx, sy);
transform: scaleX(sx);
transform: scaleY(sy);
transform: scaleZ(sz);
transform: scale3d(sx, sy, sz);

/* Rotation */
transform: rotate(angle);
transform: rotateX(angle);
transform: rotateY(angle);
transform: rotateZ(angle);
transform: rotate3d(x, y, z, angle);

/* Déformation */
transform: skew(ax, ay);
transform: skewX(ax);
transform: skewY(ay);

/* Perspective */
transform: perspective(d);

@keyframes nom {
		from { /* État initial */ }
	to { /* État final */ }
}

@keyframes slide {
	from { transform: translateX(0); }
	to { transform: translateX(100px); }
}
