commands.code:
#mon-id { color: red; }

.ma-classe { color: blue; }

p { color: green; }

:root {
	--primary-color: #3498db;
	--secondary-color: #2ecc71;
}

body {
	color: var(--primary-color);
}

.button {
	color: var(--secondary-color);
	color: white;
	padding: 10px 20px;
	border: none;
	border-radius: 5px;
	cursor: pointer;
}
