// Classe Personne
class Personne {
  #nom; // Attribut privé (à partir d'ES2022)

  // Constructeur
  constructor(nom) {
    this.#nom = nom;
  }

  // Getter pour obtenir le nom
  getNom() {
    return this.#nom;
  }

  // Setter pour modifier le nom
  setNom(nouveauNom) {
    if (nouveauNom.length > 0) {
        this.#nom = nouveauNom;
    }
  }
}

// Utilisation de la classe
const personne = new Personne('Alice');
console.log(personne.getNom()); // Affiche: Alice
personne.setNom('Bob');
console.log(personne.getNom()); // Affiche: Bob

// Classe Personne
public class Personne {
  private String nom;

  // Constructeur
  public Personne(String nom) {
    this.nom = nom;
  }

  // Getter pour obtenir le nom
  public String getNom() {
    return nom;
  }

  // Setter pour modifier le nom
  public void setNom(String nouveauNom) {
    if (nouveauNom != null && !nouveauNom.isEmpty()) {
        this.nom = nouveauNom;
    }
  }
}

// Utilisation de la classe
Personne personne = new Personne("Alice");
System.out.println(personne.getNom()); // Affiche: Alice
personne.setNom("Bob");
System.out.println(personne.getNom()); // Affiche: Bob

// Classe Personne
class Personne {
  private $nom;

  // Constructeur
  public function __construct($nom) {
    $this->nom = $nom;
  }

  // Getter pour obtenir le nom
  public function getNom() {
    return $this->nom;
  }

  // Setter pour modifier le nom
  public function setNom($nouveauNom) {
    if (!empty($nouveauNom)) {
        $this->nom = $nouveauNom;
    }
  }
}

