from abc import ABC, abstractmethod

# Classe de base Forme
class Forme(ABC):
  @abstractmethod
  def dessiner(self):
    pass

# Classe Rectangle
class Rectangle(Forme):
  def dessiner(self):
    print("Rectangle")

# Classe Carre
class Carre(Forme):
  def dessiner(self):
    print("Carré")

# Polymorphisme
def dessiner_forme(forme):
  forme.dessiner()

# Utilisation
rectangle = Rectangle()
carre = Carre()

dessiner_forme(rectangle)  # Affiche: Rectangle
dessiner_forme(carre) # Affiche: Carré

